import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate, useParams  } from "react-router-dom";
import axios from "axios";
import io from "socket.io-client";


export default function Chat() {
    const { groupId } = useParams(); 
    const backend = "http://127.0.0.1:5100";
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newMessage, setNewMessage] = useState("");
    const [typing, setTyping] = useState("");
    const [groupData, setGroupData] = useState({});
    const socket = useRef(null);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("token"); // Retrieve the token from localStorage
        const user = localStorage.getItem("username"); // Retrieve the username from localStorage

        if (token) { // Check if the token exists
            socket.current = io(backend, {
                auth: {
                    token: token, // Pass the token in the headers for authentication
                },
            });

            const fetchData = async () => {
                try {
                    const response = await axios.get(`${backend}/group/${groupId}/messages`, {
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": `Bearer ${token}` // Include the token in the request headers
                        }
                    });

                    if (response.status === 200) {
                        const data = response.data;
                        const mymsg = data.map((msg) => { return { message: msg.message, sender: msg.sender.username } });
                        setMessages(mymsg);
                    } else {
                        console.error("Failed to fetch messages");
                    }
                    const groupName = await axios.get(`${backend}/group/${groupId}`, {
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": `Bearer ${token}` // Include the token in the request headers
                        }
                    });

                    if (groupName.status === 200) {
                        const groupNameData = groupName;
                        setGroupData(groupNameData);
                    }

                } catch (error) {
                    console.error("Error fetching messages:", error);
                } finally {
                    setLoading(false);
                }
            };

            fetchData();

            // Listen for new messages
            socket.current.on("message", (newMessage) => {
                console.log("New message:", newMessage);
                setMessages((prevMessages) => [...prevMessages, newMessage]);
            });

            // Listen for typing
            socket.current.on("type", (user) => {
                // console.log("Typing:", user);
                setTyping(user, " is typing ...");
            });

            socket.current.on("stopTyping", () => {
                setTyping("");
            });

            // Cleanup on component unmount
            return () => {
                socket.current.disconnect();
            };
        }
    }, [groupId, backend]);

    useEffect(() => {

        if(!groupData || !groupData.data) return;
        const username = localStorage.getItem("username"); // Retrieve the username from localStorage
        const userId = localStorage.getItem("id"); // Retrieve the userId from localStorage
        console.log("Joining room:", groupData.data.name);
        socket.current.emit("joinRoom", { username: username, userId: userId, room: groupData.data.name, groupId: groupId, token: localStorage.getItem("token")});

    }, [groupData]);


    const handleSendMessage = async (e) => {
        e.preventDefault();

        const token = localStorage.getItem("token"); // Retrieve the token from localStorage
        const userId = localStorage.getItem("id"); // Retrieve the userId from localStorage
        const username = localStorage.getItem("username"); // Retrieve the username from localStorage


        if (token) { // Check if the token exists
            try {
                console.log("Sending message:", newMessage);
                socket.current.emit("chatMessage",
                    { message: newMessage, sender: userId, group: groupId }, groupData.data.name, username);
                setNewMessage("");
                setTyping("");

            } catch (error) {
                console.error("Error sending message:", error);
            }
        }
    };


    const handleChange = (e) => {
        const message = e.target.value;
        setNewMessage(message);

        const username = localStorage.getItem("username"); 
        socket.current.emit(`typing`, { room: groupData.data.name, username: username}); 

        if (message === "") {
            socket.current.emit(`stopTyping`, { room: groupData.data.name }); // Emit stopTyping event
        }
    };

    // user leaves the room upadte the database
    const handleLeaveRoom = async () => {
        const token = localStorage.getItem("token"); // Retrieve the token from localStorage
        const userId = localStorage.getItem("id"); // Retrieve the userId from localStorage
        try {

            const username = localStorage.getItem("username"); // Retrieve the username from localStorage
            socket.current.emit("leaveRoom", { user: userId, username: username, groupId: groupId, name: groupData.data.name });

            console.log("Left the room");
            navigate("/dashboard");

        } catch (error) {
            console.error("Error leaving the room:", error);
        }
    };


    return (
    <div className="bg-gray-900 min-h-screen flex flex-col">
      {/* Header */}
      <div className="flex items-start justify-start pl-5 py-4 text-gray-500 bg-[#282828]">
        <h1 className="text-4xl font-bold">Chat</h1>
      </div>

      {/* Chat Container */}
      <div className="container mx-auto min-h-screen flex flex-col items-center justify-center px-5">
        <div className="flex flex-col w-full max-w-2xl p-4 relative bg-gray-800/80 rounded-lg shadow-lg">
          {/* Chat Header */}
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-bold text-gray-200">Chat</h1>
            <div className="flex gap-2 justify-center items-center">
              <Link
                to="/dashboard"
                className="px-3 py-1 text-sm font-semibold text-gray-200 bg-red-600 rounded hover:bg-red-800 transition duration-200 ease-in-out"
                onClick={() => console.log("Leaving Room")}
              >
                Leave
              </Link>
              <Link
                to="/dashboard"
                className="px-3 py-1 text-sm font-semibold text-gray-200 bg-gray-500 rounded hover:bg-slate-900 transition duration-200 ease-in-out"
              >
                Back
              </Link>
            </div>
          </div>

          {/* Typing Indicator */}
          {typing && <div className="text-gray-200 text-sm mt-2">{typing}</div>}

          {/* Messages Section */}
          <div className="flex flex-col mt-4 space-y-2 overflow-y-auto max-h-80 p-2 bg-gray-700 rounded-lg">
            {loading ? (
              <div className="text-gray-400 text-center">Loading messages...</div>
            ) : messages.length > 0 ? (
              messages.map((msg, index) => (
                <div
                  key={index}
                  className={`flex items-center ${
                    msg.username === currentUser ? "justify-end" : "justify-start"
                  }`}
                >
                  <div
                    className={`p-3 rounded-lg max-w-xs break-words ${
                      msg.username === currentUser
                        ? "bg-blue-600 text-white self-end"
                        : "bg-gray-300 text-black self-start"
                    }`}
                  >
                    <div className="text-sm font-semibold">
                      {msg.username !== currentUser && msg.username}
                    </div>
                    <div className="text-sm">{msg.message}</div>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-gray-400 text-center">No messages yet</div>
            )}
          </div>

          {/* Message Input */}
          <div className="w-full mt-4">
            <form
              onSubmit={(e) => {
                e.preventDefault();
                handleSendMessage();
              }}
              className="w-full flex items-center gap-2"
            >
              <input
                type="text"
                name="newMessage"
                value={newMessage}
                onChange={handleChange}
                className="flex-1 h-10 pl-3 text-base placeholder-gray-400 text-black border rounded-lg focus:shadow-outline focus:outline-none"
                placeholder="Type here..."
              />
              <button
                type="submit"
                className="text-base px-4 py-2 rounded-lg bg-gray-300/60 text-gray-200 hover:text-gray-900/70 hover:bg-gray-500 focus:outline-none focus:text-gray-900/70 focus:shadow-outline transition duration-200 ease-in-out"
              >
                Send
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};
