import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import io from "socket.io-client";
import { ArrowLeft } from "lucide-react";

export default function Chat() {
    const { groupId } = useParams(); 
    const backend = "http://127.0.0.1:5100";
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newMessage, setNewMessage] = useState("");
    const [typing, setTyping] = useState("");
    const [groupData, setGroupData] = useState({ name: "", members: [] });
    const socket = useRef(null);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("token");

        if (token) {
            socket.current = io(backend, {
                auth: { token: token },
            });

            const fetchData = async () => {
                try {
                    const response = await axios.get(`${backend}/group/${groupId}/messages`, {
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": `Bearer ${token}`
                        }
                    });

                    if (response.status === 200) {
                        setMessages(response.data.map(msg => ({ message: msg.message, sender: msg.sender.username })));
                    }

                    const groupResponse = await axios.get(`${backend}/group/${groupId}`, {
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": `Bearer ${token}`
                        }
                    });

                    if (groupResponse.status === 200) {
                        setGroupData(groupResponse.data);
                    }
                } catch (error) {
                    console.error("Error fetching data:", error);
                } finally {
                    setLoading(false);
                }
            };

            fetchData();

            socket.current.on("message", (newMessage) => {
                setMessages((prevMessages) => [...prevMessages, newMessage]);
            });

            socket.current.on("type", (user) => {
                setTyping(`${user} is typing...`);
            });

            socket.current.on("stopTyping", () => {
                setTyping("");
            });

            return () => {
                socket.current.disconnect();
            };
        }
    }, [groupId]);

    return (
        <div className="bg-gray-900 min-h-screen text-white">
            <div className="flex items-center px-6 py-4 bg-gray-800 shadow-md gap-4">
                <button onClick={() => navigate(-1)} className="text-gray-400 hover:text-white">
                    <ArrowLeft size={24} />
                </button>
                <h1 className="text-4xl font-bold">{groupData.name ? groupData.name : "Chat"}</h1>
            </div>
            <div className="container mx-auto min-h-screen min-w-full">
                <div className="flex flex-col items-center mt-4">
                    <h2 className="text-xl font-semibold">Group Members:</h2>
                    <ul className="text-gray-300">
                        {groupData.members.length > 0 ? (
                            groupData.members.map((member, index) => (
                                <li key={index}>{member.username}</li>
                            ))
                        ) : (
                            <p>No members found.</p>
                        )}
                    </ul>
                </div>
                <div className="flex items-center justify-center min-h-screen min-w-full px-5">
                    <div className="flex flex-col w-full max-w-2xl p-4 relative bg-gray-800/80 rounded-lg shadow-lg">
                        {typing && <div className="text-gray-200 text-sm">{typing}</div>}
                        <div className="flex flex-col mt-4 overflow-y-auto h-60 bg-gray-700 rounded p-3">
                            {loading ? (
                                <div className="text-gray-400 text-center">Loading messages...</div>
                            ) : messages.length > 0 ? (
                                messages.map((msg, index) => (
                                    <div key={index} className="text-gray-200 mb-2">
                                        <strong>{msg.sender}:</strong> {msg.message}
                                    </div>
                                ))
                            ) : (
                                <div className="text-gray-400 text-center">No messages yet</div>
                            )}
                        </div>
                        <form className="mt-4 flex" onSubmit={(e) => e.preventDefault()}>
                            <input
                                type="text"
                                value={newMessage}
                                onChange={(e) => setNewMessage(e.target.value)}
                                className="flex-1 p-2 text-black rounded-lg"
                                placeholder="Type a message..."
                            />
                            <button
                                className="ml-2 px-4 py-2 bg-blue-500 rounded-lg hover:bg-blue-700"
                                onClick={() => setMessages([...messages, { sender: "You", message: newMessage }])}
                            >
                                Send
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}
