import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import io from "socket.io-client";
import { ArrowLeft } from "lucide-react";
import moment from "moment";

export default function Chat() {
  const { groupId } = useParams(); 
    const backend = "http://localhost:8089";
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newMessage, setNewMessage] = useState("");
    const [typing, setTyping] = useState("");
    const [groupData, setGroupData] = useState({});
    const socket = useRef(null);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("token"); // Retrieve the token from localStorage
        const user = localStorage.getItem("username"); // Retrieve the username from localStorage

        if (token) { // Check if the token exists
            socket.current = io(backend, {
                auth: {
                    token: token, // Pass the token in the headers for authentication
                },
            });

            const fetchData = async () => {
                try {
                    const response = await axios.get(`${backend}/group/${groupId}/messages`, {
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": `Bearer ${token}` // Include the token in the request headers
                        }
                    });

                    if (response.status === 200) {
                        const data = response.data;
                        const mymsg = data.map((msg) => { return { message: msg.message, sender: msg.sender.username } });
                        setMessages(mymsg);
                    } else {
                        console.error("Failed to fetch messages");
                    }
                    const groupName = await axios.get(`${backend}/group/${groupId}`, {
                        headers: {
                            "Content-Type": "application/json",
                            "Authorization": `Bearer ${token}` // Include the token in the request headers
                        }
                    });

                    if (groupName.status === 200) {
                        const groupNameData = groupName;
                        setGroupData(groupNameData);
                    }

                } catch (error) {
                    console.error("Error fetching messages:", error);
                } finally {
                    setLoading(false);
                }
            };

            fetchData();

            // Listen for new messages
            socket.current.on("message", (newMessage) => {
                console.log("New message:", newMessage);
                setMessages((prevMessages) => [...prevMessages, newMessage]);
            });

            // Listen for typing
            socket.current.on("type", (user) => {
                // console.log("Typing:", user);
                setTyping(user, " is typing ...");
            });

            socket.current.on("stopTyping", () => {
                setTyping("");
            });

            // Cleanup on component unmount
            return () => {
                socket.current.disconnect();
            };
        }
    }, [groupId, backend]);

    useEffect(() => {

        if(!groupData || !groupData.data) return;
        const username = localStorage.getItem("username"); // Retrieve the username from localStorage
        const userId = localStorage.getItem("id"); // Retrieve the userId from localStorage
        console.log("Joining room:", groupData.data.name);
        socket.current.emit("joinRoom", { username: username, userId: userId, room: groupData.data.name, groupId: groupId, token: localStorage.getItem("token")});

    }, [groupData]);


    const handleSendMessage = async (e) => {
        e.preventDefault();

        const token = localStorage.getItem("token"); // Retrieve the token from localStorage
        const userId = localStorage.getItem("id"); // Retrieve the userId from localStorage
        const username = localStorage.getItem("username"); // Retrieve the username from localStorage


        if (token) { // Check if the token exists
            try {
                console.log("Sending message:", newMessage);
                socket.current.emit("chatMessage",
                    { message: newMessage, sender: userId, group: groupId }, groupData.data.name, username);
                setNewMessage("");
                setTyping("");

            } catch (error) {
                console.error("Error sending message:", error);
            }
        }
    };


    const handleChange = (e) => {
        const message = e.target.value;
        setNewMessage(message);

        const username = localStorage.getItem("username"); // Retrieve the username from localStorage
        socket.current.emit(`typing`, { room: groupData.data.name, username: username}); // Emit typing event with the username

        if (message === "") {
            socket.current.emit(`stopTyping`, { room: groupData.data.name }); // Emit stopTyping event
        }
    };

    // user leaves the room upadte the database
    const handleLeaveRoom = async () => {
        const token = localStorage.getItem("token"); // Retrieve the token from localStorage
        const userId = localStorage.getItem("id"); // Retrieve the userId from localStorage
        try {

            const username = localStorage.getItem("username"); // Retrieve the username from localStorage
            socket.current.emit("leaveRoom", { user: userId, username: username, groupId: groupId, name: groupData.data.name });

            console.log("Left the room");
            navigate("/dashboard");

        } catch (error) {
            console.error("Error leaving the room:", error);
        }
    };


  return (
    <div className="bg-gray-900 min-h-screen flex flex-col items-center justify-center p-4">
      <div className="w-full max-w-2xl bg-gray-800 p-6 rounded-lg shadow-lg">
        <div className="flex justify-between items-center mb-4">
          <button onClick={() => navigate(-1)} className="text-gray-400 hover:text-white">
            <ArrowLeft size={24} />
          </button>
          <h1 className="text-xl font-bold text-white">Group Chat</h1>
          <Link to="/dashboard" className="text-red-400 hover:underline">Leave</Link>
        </div>
        {typing && <div className="text-sm text-gray-300 mb-2">{typing}</div>}
        <div className="overflow-y-auto max-h-[500px] space-y-3 p-2 bg-gray-700 rounded-lg">
          {loading ? (
            <div className="text-gray-400 text-center">Loading messages...</div>
          ) : messages.length > 0 ? (
            messages.map((msg, index) => (
              <div
                key={index}
                className={`flex ${msg.username === currentUser ? "justify-end" : "justify-start"}`}
              >
                <div className={`p-3 rounded-lg max-w-xs break-words ${msg.username === currentUser ? "bg-blue-600 text-white" : "bg-gray-300 text-black"}`}>
                  {msg.username !== currentUser && <div className="text-xs font-semibold text-gray-700">{msg.username}</div>}
                  <div className="text-sm">{msg.message}</div>
                  <div className="text-xs text-gray-500 mt-1 text-right">{msg.time}</div>
                </div>
              </div>
            ))
          ) : (
            <div className="text-gray-400 text-center">No messages yet</div>
          )}
        </div>
        <form onSubmit={handleSendMessage} className="mt-4 flex gap-2">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            className="flex-1 p-2 rounded-lg border-none focus:outline-none text-black"
            placeholder="Type a message..."
          />
          <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-700">Send</button>
        </form>
      </div>
    </div>
  );
}
