import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import io from "socket.io-client";

export default function Chat() {
  const { groupId } = useParams();
  const backend = "http://127.0.0.1:5100";
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newMessage, setNewMessage] = useState("");
  const [typing, setTyping] = useState("");
  const [groupData, setGroupData] = useState({});
  const socket = useRef(null);
  const navigate = useNavigate();
  const currentUser = localStorage.getItem("username");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      socket.current = io(backend, { auth: { token } });
      const fetchData = async () => {
        try {
          const response = await axios.get(`${backend}/group/${groupId}/messages`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          if (response.status === 200) {
            setMessages(response.data.map(msg => ({ message: msg.message, sender: msg.sender.username })));
          }
        } catch (error) {
          console.error("Error fetching messages:", error);
        } finally {
          setLoading(false);
        }
      };
      fetchData();
      socket.current.on("message", newMessage => {
        setMessages(prevMessages => [...prevMessages, newMessage]);
      });
      return () => socket.current.disconnect();
    }
  }, [groupId]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;
    socket.current.emit("chatMessage", { message: newMessage, sender: currentUser, group: groupId });
    setNewMessage("");
  };

  return (
    <div className="bg-gray-900 min-h-screen flex flex-col">
      <div className="flex items-start justify-start pl-5 py-4 text-gray-500 bg-[#282828]">
        <h1 className="text-4xl font-bold">Chat</h1>
      </div>
      <div className="container mx-auto flex flex-col items-center justify-center px-5">
        <div className="flex flex-col w-full max-w-2xl p-4 bg-gray-800 rounded-lg shadow-lg">
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-bold text-gray-200">Chat</h1>
            <Link
              to="/dashboard"
              className="px-3 py-1 text-sm font-semibold text-gray-200 bg-red-600 rounded hover:bg-red-800"
            >
              Leave
            </Link>
          </div>
          <div className="mt-4 space-y-2 overflow-y-auto max-h-80 p-2 bg-gray-700 rounded-lg">
            {loading ? (
              <div className="text-gray-400 text-center">Loading messages...</div>
            ) : messages.length > 0 ? (
              messages.map((msg, index) => (
                <div
                  key={index}
                  className={`flex ${msg.sender === currentUser ? "justify-end" : "justify-start"}`}
                >
                  <div
                    className={`p-3 rounded-lg max-w-xs break-words ${
                      msg.sender === currentUser
                        ? "bg-blue-600 text-white"
                        : "bg-gray-300 text-black"
                    }`}
                  >
                    {msg.sender !== currentUser && (
                      <div className="text-sm font-semibold">{msg.sender}</div>
                    )}
                    <div className="text-sm">{msg.message}</div>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-gray-400 text-center">No messages yet</div>
            )}
          </div>
          <form onSubmit={handleSendMessage} className="w-full mt-4 flex items-center gap-2">
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              className="flex-1 h-10 pl-3 text-base placeholder-gray-400 text-black border rounded-lg"
              placeholder="Type here..."
            />
            <button
              type="submit"
              className="px-4 py-2 rounded-lg bg-gray-300 text-gray-900 hover:bg-gray-500"
            >
              Send
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
