import { useEffect, useState } from "react";
import axios from "axios";

const GroupDetails = ({ groupId }) => {
    const [groupData, setGroupData] = useState(null);
    const [members, setMembers] = useState([]);

    useEffect(() => {
        // Fetch group data
        axios.get(`/api/groups/${groupId}`)
            .then(response => {
                setGroupData(response.data);
            })
            .catch(error => console.error("Error fetching group data:", error));

        // Fetch members
        axios.get(`/api/groups/${groupId}/members`)
            .then(response => {
                setMembers(response.data);
            })
            .catch(error => console.error("Error fetching members:", error));
    }, [groupId]);

    return (
        <div className="flex flex-col items-center justify-center mb-4">
            <h2 className="text-xl font-semibold text-gray-200">
                Group: {groupData?.name}
            </h2>
            <h4 className="text-gray-200 text-sm">
                Members: {groupData?.members?.length}
            </h4>
            <div className="mt-4">
                <h3 className="text-lg font-semibold text-gray-200">Member Names:</h3>
                <ul className="text-gray-200">
                    {members.length > 0 ? (
                        members.map(member => (
                            <li key={member._id} className="text-sm">
                                {member.username}
                            </li>
                        ))
                    ) : (
                        <li className="text-sm">No members found</li>
                    )}
                </ul>
            </div>
        </div>
    );
};

export default GroupDetails;
